import React from 'react'
import Logo from '../../LOGO.png'
import ReactDOM from 'react-dom';
import firebase from '../../firebase.config'
import { createUserWithEmailAndPassword } from 'firebase/auth';



export class RegFields extends React.Component{
    
    constructor(props){
        super(props);
        
    }

    state = {
        Email : "",
        password:"",
        Fname:"",
        Lname:"",
        error:" ",

    }

    //Put the Email and Password in a variable to sign up
    handleMailChange = (event)=> {
        this.setState({ Email : event.currentTarget.value});
    }

    handlePassChange = (event)=> {
        this.setState({ password : event.currentTarget.value});
    }
  

    //handleSignupClick = (event) => {
      // event.preventDefault();
        //createUserWithEmailAndPassword(auth,this.state.Email,this.state.password);



    //}


    render(){
        return (
            <div className="regFields">
            <h1>Create New Account</h1>

            <div className="logo">
                <img src={Logo} />
            </div>
            <div className="formGroup">
                

                <div className="name">
                    <div className="formItem">
                    
                        <input type="text" placeholder='First Name' />
                    </div>

                    <div className="formItem">
                    
                        <input type="text" placeholder='Last Name' />
                    </div>
               </div>

                <div className="formItem">
                    
                    <input onChange={this.handleMailChange} type="email" placeholder='Email' />
                </div>

                <div className="formItem">
                    
                    <input onChange={this.handleMailChange} type="password" placeholder='Password'/>
                </div>
                

             
                <p>{error}</p>

                
                
               
            </div>

            <div className="footer">
                <button className="loginBtn" >Register</button>
            </div>
            </div>
            )
    }
}
