import React from 'react'

export class MainContainer extends React.Component{

    state ={
        logActive: true
    }

    constructor(props){
        super(props);
    }

    handleChange= ()=>{
        this.setState({logActive: !this.state.logActive})
    }

    render(){
        const logClasses = `navBtn ${logActive?'active-state':''}`;
        const regClasses = `navBtn ${!logActive?'active-state':''}`
        return(
        <>
        <div className="mainContainer">
            <div className="header">
                <button className={logClasses} id="log" >Login</button>
                <button className={regClasses} id="reg" >Register</button>
            </div>
            <div id="core">
               { this.state.logActive ? <LoginFields active={logActive} onChange={this.handleChange} />: <RegFields active={!logActive}/>}
            </div>
        </div>
        </>
        )
    }
}
