import React from 'react';

import passIcon from '../../arcticons_password.png'
import userIcon from '../../Group.png'
import Logo from '../../LOGO.png'

export class LoginFields extends React.Component{

    state = {
        active: this.props.active
    }
    constructor(props){
        super(props);
    }
    handleClick = ()=>{
        document.getElementById('log').classList.toggle('active-state');
        document.getElementById('reg').classList.toggle('active-state');
        this.setState({active: false})
        console.log(this.state.active)
    }

    render(){
        return <div className="loginFields">
                 <div className="logo">
                <img src={Logo} />
            </div>

            <div className="formGroup">


                <div className="formItem">
                    <img src={userIcon} />
                    <input type="text" placeholder='Username' />
                </div>

                <div className="formItem">
                    <img src={passIcon} alt="" />
                    <input type="password" placeholder='Password'/>
                </div>



                <button className="forgotPass">Forgot your password?</button>
            </div>

            <div className="footer">
                <button className="loginBtn" onClick={this.props.handleClick}>Log in</button>
            </div>
                </div>   
    }
}
