import React from 'react'
import {RegFields} from './index';
import {LoginFields} from './index';




export class MainContainer extends React.Component{

    state ={
        logActive: true
    }

    constructor(props){
        super(props);
    }

    displayReg = () =>{
        this.setState({logActive: false})
    }

    displayLog = () => {
        this.setState({logActive: true})
    }

    render(){
        const logClasses = `navBtn ${this.logActive?'active-state':''}`;
        const regClasses = `navBtn ${!this.logActive?'active-state':''}`
        return(
        <>
        <div className="mainContainer">
            <div className="header">
                <button className={logClasses} id="log" onClick={this.displayReg}>Login</button>
                <button className={regClasses} id="reg" onClick={this.displayLog}>Register</button>
            </div>
            <div id="core">
               { this.state.logActive ? <LoginFields active={this.state.logActive} onChange={this.handleChange} />: <RegFields active={!this.state.logActive}/>}
            </div>
        </div>
        </>
        )
    }
}
