import React from 'react'
import Logo from '../../LOGO.png'
import ReactDOM from 'react-dom';

export class RegFields extends React.Component{

    constructor(props){
        super(props);
        state = {
            active: true,
        }
    }

    regClick(){
        document.getElementById('reg').classList.toggle('activeState');
        document.getElementById('log').classList.toggle('activeState');
        this.setState({active: false});
    }

    render(){
        return (
            <div className="regFields">
            <h1>Create New Account</h1>
            <div className="formGroup">


                <div className="formItem">

                    <input type="text" placeholder='First Name' />
                </div>

                <div className="formItem">

                    <input type="text" placeholder='Last Name' />
                </div>

                <div className="formItem">

                    <input type="email" placeholder='Email' />
                </div>

                <div className="formItem">

                    <input type="password" placeholder='Password'/>
                </div>



                <button className="forgotPass">Forgot your password?</button>
            </div>

            <div className="footer">
                <button className="loginBtn" onClick={()=>{this.regClick()}}>Register</button>
            </div>
            </div>
            )
    }
}
